sp_widget.client_script:
function(spUtil, $rootScope,$scope) {
	/* widget controller */
	var c = this;

	c.selectIncident = function(index){
		//spUtil.addInfoMessage("Selected:"+c.data.incidents[index].number);
		$rootScope.id = c.data.incidents[index].sys_id;
		$rootScope.$emit('getIncident');
	}

	c.newIncident = function(){

	}

	$rootScope.$on("updateList", function(event, data){
		for(var x=0; x<c.data.incidents.length; x++){
			if(c.data.incidents[x].number == $rootScope.number){
				c.data.incidents[x].short_description = $rootScope.short_description;
			}
		}
	});

}

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */

	data.incidents = [];
	data.head = "Incidents";

	var inc = new GlideRecord('incident');
	inc.addEncodedQuery('active=true');
	inc.setLimit(5);
	inc.query();
	while(inc.next()){
		var record = {};
		$sp.getRecordDisplayValues(record, inc, "number, short_description, priority, assignment_group, sys_id");
		data.incidents.push(record);
	}
})();
